/* Function  structure for function  `concat_name` */

/*!50003 DROP FUNCTION IF EXISTS `concat_name` */;
DELIMITER $$

/*!50003 CREATE DEFINER=`web_user`@`%` FUNCTION `concat_name`(iname TEXT, ilevel INT) RETURNS text CHARSET utf8
BEGIN
	DECLARE i INT;
	SET i = 0;
	WHILE i < ilevel DO
		SET iname = CONCAT('-- ',iname);
		SET i = i + 1;
	END WHILE;
  RETURN iname;
END */$$
DELIMITER ;

/* Function  structure for function  `f_replace_xml_bad_char` */

/*!50003 DROP FUNCTION IF EXISTS `f_replace_xml_bad_char` */;
DELIMITER $$

/*!50003 CREATE DEFINER=`web_user`@`%` FUNCTION `f_replace_xml_bad_char`(p_string TEXT) RETURNS text CHARSET utf8
BEGIN
SET p_string = REPLACE(p_string,'<','&amp;lt;');
SET p_string = REPLACE(p_string,'>','&amp;gt;');
SET p_string = REPLACE(p_string,'"','&amp;#34');
SET p_string = REPLACE(p_string,'''','&amp;#39');
RETURN p_string;
END */$$
DELIMITER ;

/* Procedure structure for procedure `sp_control_bizdone_step_log` */

/*!50003 DROP PROCEDURE IF EXISTS  `sp_control_bizdone_step_log` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`web_user`@`%` PROCEDURE `sp_control_bizdone_step_log`(IN village_id INT)
BEGIN
	DECLARE record_id INT;
	DECLARE done INT DEFAULT FALSE;
	DECLARE cur_record CURSOR FOR SELECT PK_RECORD FROM t_r3_record WHERE FK_VILLAGE_ID = village_id AND C_DELETED <> 1;
	DECLARE CONTINUE HANDLER FOR NOT FOUND SET done = TRUE;
	DELETE FROM t_r3_bizdone_step_log WHERE FK_VILLAGE_ID = village_id;
	OPEN cur_record;
	record_loop: LOOP
		FETCH cur_record INTO record_id;
		IF done THEN
			SET done:= FALSE;
			CLOSE cur_record;  
			LEAVE record_loop;				
		END IF;
		CALL sp_load_bizdone_step_log(record_id);
	END LOOP record_loop;		
END */$$
DELIMITER ;

/* Procedure structure for procedure `sp_control_save_history_stat` */

/*!50003 DROP PROCEDURE IF EXISTS  `sp_control_save_history_stat` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`web_user`@`%` PROCEDURE `sp_control_save_history_stat`()
BEGIN
	DECLARE v_spec_code VARCHAR(100);
	DECLARE done INT DEFAULT FALSE;
	DECLARE v_village_id INT;
	DECLARE cur_spec CURSOR
	FOR SELECT trim(ls.C_CODE)
		FROM t_cores_listtype lt 
			INNER JOIN t_cores_list ls
			ON lt.PK_LISTTYPE = ls.FK_LISTTYPE
		WHERE lt.C_CODE = 'DANH_MUC_LINH_VUC';
	DECLARE cur_village CURSOR FOR SELECT PK_OU FROM t_cores_ou WHERE C_LEVEL = 3;
	DECLARE CONTINUE HANDLER FOR NOT FOUND SET done = TRUE;
	DELETE FROM t_r3_record_history_stat WHERE DATEDIFF(NOW(), C_HISTORY_DATE)=0;
	OPEN cur_spec;
	spec_loop: LOOP
		FETCH cur_spec INTO v_spec_code;
		IF done THEN
			CLOSE cur_spec;  
			LEAVE spec_loop;
		END IF;
		OPEN cur_village;
		village_loop: LOOP
			FETCH cur_village INTO v_village_id;
			IF done THEN
				SET done:= FALSE;
				CLOSE cur_village;  
				LEAVE village_loop;				
			END IF;
			CALL sp_save_record_history_stat(v_spec_code, v_village_id);
		END LOOP village_loop;
		CALL sp_save_record_history_stat(v_spec_code, 0);
	END LOOP spec_loop ;		
END */$$
DELIMITER ;

/* Procedure structure for procedure `sp_load_bizdone_step_log` */

/*!50003 DROP PROCEDURE IF EXISTS  `sp_load_bizdone_step_log` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`web_user`@`%` PROCEDURE `sp_load_bizdone_step_log`(IN record_id INT)
BEGIN
	DECLARE count_step INT;
	DECLARE i INT;
        
        DECLARE group_code VARCHAR(200);
        DECLARE finish_date  VARCHAR(200);
        DECLARE step_time  INT;
	    DECLARE temp  INT;
        DECLARE days_exceed INT;
        DECLARE task_code VARCHAR(200);
        DECLARE user_code VARCHAR(200);
        DECLARE step_begin_date VARCHAR(200);
        DECLARE step_deadline_date VARCHAR(200);
        DECLARE village_id INT;
		DECLARE check_task INT;
	SET i = 1;
	SET count_step = (SELECT
			  ExtractValue(C_XML_PROCESSING,'count(//step/to_group_code)') AS C_COUNT
			FROM t_r3_record
			WHERE PK_RECORD = record_id);
	
	INSERT_BD_STEP: LOOP
		IF i <= count_step THEN
		
			SET finish_date = (SELECT ExtractValue(C_XML_PROCESSING,CONCAT('//step[to_group_code][position()=',i,']/datetime'))
						FROM t_r3_record WHERE PK_RECORD = record_id);
			SET task_code = (SELECT ExtractValue(C_XML_PROCESSING,CONCAT('//step[to_group_code][position()=',i,']/@code'))
						FROM t_r3_record WHERE PK_RECORD = record_id);
			SET user_code = (SELECT ExtractValue(C_XML_PROCESSING,CONCAT('//step[to_group_code][position()=',i,']/user_code'))
						FROM t_r3_record WHERE PK_RECORD = record_id);
			SET step_time = (SELECT COUNT(C_STEP_TIME) FROM t_r3_user_task WHERE C_TASK_CODE = task_code);
			
			SET check_task = (SELECT INSTR(task_code,'NHAN_BIEN_LAI_NOP_THUE'));
	  
			IF check_task <= 0 THEN
			
				IF step_time > 0 THEN
					SET step_time = (SELECT MAX(C_STEP_TIME) FROM t_r3_user_task WHERE C_TASK_CODE = task_code);
				ELSE
					SET step_time = -1;
				END IF;
				
				IF i = 1 THEN 
					SET step_begin_date = (SELECT C_RECEIVE_DATE FROM t_r3_record WHERE PK_RECORD = record_id);
				ELSE
					SET step_begin_date = (SELECT ExtractValue(C_XML_PROCESSING,CONCAT('//step[to_group_code][position()=',(i-1),']/datetime'))
							FROM t_r3_record WHERE PK_RECORD = record_id);
				END IF;
				
				
				IF i = 1 THEN
				
					SET group_code = (SELECT C_GROUP_CODE FROM t_r3_user_task WHERE C_TASK_CODE = task_code LIMIT 1);
				ELSE
					SET temp = (SELECT CHAR_LENGTH(ExtractValue(C_XML_PROCESSING,CONCAT('//step[to_group_code][position()=',(i-1),']/to_group_code')))
								FROM t_r3_record WHERE PK_RECORD = record_id);
					IF temp > 0
					THEN
						SET group_code = (SELECT ExtractValue(C_XML_PROCESSING,CONCAT('//step[to_group_code][position()=',(i-1),']/to_group_code'))
								FROM t_r3_record WHERE PK_RECORD = record_id);
					ELSE
						SET group_code = (SELECT C_GROUP_CODE FROM t_r3_user_task WHERE C_TASK_CODE = task_code LIMIT 1);
					END IF;
					
				END IF;
				
				
				IF step_time = -1 THEN
					SET step_deadline_date = finish_date;
				ELSE
					SET @rownum=0;
						SET step_deadline_date =  (SELECT
									  MAX(T.C_DATE)
										FROM (SELECT
											C_DATE,
											 @rownum:=@rownum+1 AS rn
											  FROM t_cores_calendar
											  WHERE DATEDIFF(C_DATE,step_begin_date) >= 0
											  AND C_OFF = 0
											  ORDER BY C_DATE) T WHERE rn <= (step_time+1));
				END IF;
				
				SET village_id = (SELECT CASE WHEN C_LEVEL = 3 THEN PK_OU ELSE 0 END AS C_VILLAGE_ID
							FROM t_cores_ou
							WHERE PK_OU = (SELECT
									 FK_OU
									   FROM t_cores_user
									   WHERE C_LOGIN_NAME = user_code)
									   
							);
							 
				SET days_exceed = (SELECT CASE WHEN DATEDIFF(finish_date,step_deadline_date) > 0
												THEN 
												 (SELECT -1 * (SELECT COUNT(*) 
														FROM t_cores_calendar 
														WHERE DATEDIFF(C_DATE, finish_date)<=0
														AND DATEDIFF(C_DATE, step_deadline_date)>0
														AND C_OFF = 0))
												ELSE
												(SELECT (SELECT COUNT(*) 
														FROM t_cores_calendar 
														WHERE DATEDIFF(C_DATE, finish_date)>=0
														AND DATEDIFF(C_DATE, step_deadline_date)<0
														AND C_OFF = 0))
												END AS C_DAYS_EXCEED);
						C_GROUP_CODE, 
						C_FINISH_DATE, 
						C_DAYS_EXCEED, 
						C_TASK_CODE, 
						FK_RECORD, 
						C_STEP_DEADLINE_DATE, 
						FK_VILLAGE_ID
					)
					VALUES
					(
						group_code, 
						finish_date, 
						days_exceed, 
						task_code, 
						record_id, 
						step_deadline_date, 
						village_id
					);
			END IF;
			
			
			SET i = i + 1;
		      
		    ITERATE INSERT_BD_STEP;
		 END IF;
		 LEAVE INSERT_BD_STEP;
	  END LOOP INSERT_BD_STEP;		
END */$$
DELIMITER ;

/* Procedure structure for procedure `sp_save_record_history_stat` */

/*!50003 DROP PROCEDURE IF EXISTS  `sp_save_record_history_stat` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`web_user`@`%` PROCEDURE `sp_save_record_history_stat`(v_spec_code VARCHAR(20), v_village_id INT)
BEGIN
	DECLARE v_tong_tiep_nhan INT;
	DECLARE v_tong_tiep_nhan_trong_thang INT;
	DECLARE v_dang_thu_ly INT;
	DECLARE v_dang_cho_tra_kq INT;
	DECLARE v_da_tra_kq INT;
	DECLARE v_da_tra_kq_truoc_han INT;
	DECLARE v_da_tra_kq_dung_han INT;
	DECLARE v_da_tra_kq_qua_han INT;
	DECLARE v_dang_thu_ly_dung_tien_do INT;
	DECLARE v_dang_thu_ly_cham_tien_do INT;
	DECLARE v_phai_bo_sung INT;
	DECLARE v_thu_ly_qua_han INT;
	DECLARE v_cong_dan_rut INT;
	DECLARE v_bi_tu_choi INT;
	DECLARE v_thue INT;
	DECLARE v_current_month INT;
	DECLARE v_current_year INT;
	DECLARE v_current_date DATETIME;
	SET v_current_date = NOW();
	SET v_current_month = MONTH(v_current_date);
	SET v_current_year = YEAR(v_current_date);
	IF v_village_id IS NULL THEN 
        SET v_village_id = 0;
    END IF;
	#Tong so ho so tiep nhan, tinh den ngay bao cao (HISTORY_DATE)
	SET v_tong_tiep_nhan 
        = (SELECT COUNT(*) 
            FROM view_record r
                LEFT JOIN t_r3_record_type rt 
                ON r.fk_record_type = rt.pk_record_type
            WHERE rt.C_SPEC_CODE = v_spec_code
                AND r.FK_VILLAGE_ID = v_village_id
    );
	#So ho so tiep nhan trong thang cua ngay bao cao (HISTORY_DATE)		
	SET v_tong_tiep_nhan_trong_thang 
        = (SELECT COUNT(*) 
            FROM view_record r
                LEFT JOIN t_r3_record_type rt 
                ON r.fk_record_type = rt.pk_record_type
            WHERE 
                MONTH(r.C_RECEIVE_DATE) = v_current_month
                AND YEAR(r.C_RECEIVE_DATE) = v_current_year
                AND rt.C_SPEC_CODE = v_spec_code 
                AND r.FK_VILLAGE_ID = v_village_id
    );
	#So ho so dang thu ly, tinh den ngay bao cao					
	SET v_dang_thu_ly 
        = (SELECT COUNT(*)
            FROM view_processing_record r
				LEFT JOIN t_r3_record_type rt 
				ON r.fk_record_type = rt.pk_record_type
            WHERE 
                r.C_BIZ_DAYS_EXCEED IS NULL
				AND (r.C_NEXT_TASK_CODE IS NOT NULL)
				AND r.C_BIZ_DONE_DATE IS NULL
				AND rt.C_SPEC_CODE = v_spec_code
				AND r.C_IS_PAUSING <> 1
				AND r.FK_VILLAGE_ID = v_village_id 
    );
    #So ho so dang cho tra ket qua, tinh den ngay bao cao            
	SET v_dang_cho_tra_kq 
        = (SELECT COUNT(*)    
            FROM view_processing_record r
                LEFT JOIN t_r3_record_type rt 
                ON r.fk_record_type = rt.pk_record_type
            WHERE 
                r.C_BIZ_DONE_DATE IS NOT NULL
                AND r.C_BIZ_DAYS_EXCEED IS NOT NULL
                AND r.C_CLEAR_DATE IS NULL
                AND rt.C_SPEC_CODE = v_spec_code
                AND r.FK_VILLAGE_ID = v_village_id
    );
    #Tong so hoso da tra KQ TRONG THANG cua ngay bao cao
	SET v_da_tra_kq
		= (SELECT COUNT(*)
			FROM view_record r
				LEFT  JOIN t_r3_record_type rt 
				ON r.fk_record_type = rt.pk_record_type
            WHERE 
				YEAR(r.C_CLEAR_DATE) = v_current_year
				AND MONTH(r.C_CLEAR_DATE) = v_current_month
				AND r.C_REJECTED = 0
				AND r.C_CLEAR_DATE IS NOT NULL
				AND r.C_BIZ_DAYS_EXCEED IS NOT NULL
				AND rt.C_SPEC_CODE = v_spec_code
				AND r.FK_VILLAGE_ID = v_village_id
	);
	#Tong so hoso da TRA KET QUA TRONG THANG, truoc han
	SET v_da_tra_kq_truoc_han 
		= (SELECT COUNT(*)
			FROM view_record r
				LEFT  JOIN t_r3_record_type rt 
				ON r.fk_record_type = rt.pk_record_type
            WHERE 
				YEAR(r.C_CLEAR_DATE) = v_current_year
				AND MONTH(r.C_CLEAR_DATE) = v_current_month
				AND r.C_REJECTED = 0
				AND r.C_CLEAR_DATE IS NOT NULL
				AND r.C_BIZ_DAYS_EXCEED > 0 
				AND rt.C_SPEC_CODE = v_spec_code
				AND r.FK_VILLAGE_ID = v_village_id				
	);
	#Tong so hoso da TRA KET QUA TRONG THANG, dung han				
	SET v_da_tra_kq_dung_han 
		= (SELECT COUNT(*)
			FROM view_record r
				LEFT  JOIN t_r3_record_type rt 
				ON r.fk_record_type = rt.pk_record_type
            WHERE 
				YEAR(r.C_CLEAR_DATE) = v_current_year
				AND MONTH(r.C_CLEAR_DATE) = v_current_month
				AND r.C_REJECTED = 0
				AND r.C_CLEAR_DATE IS NOT NULL
				AND r.C_BIZ_DAYS_EXCEED = 0 
				AND rt.C_SPEC_CODE = v_spec_code
				AND r.FK_VILLAGE_ID = v_village_id				
	);
	#Tong so hoso da TRA KET QUA TRONG THANG, Qua han			
	SET v_da_tra_kq_qua_han 
		= (SELECT COUNT(*)
			FROM view_record r
				LEFT  JOIN t_r3_record_type rt 
				ON r.fk_record_type = rt.pk_record_type
            WHERE 
				YEAR(r.C_CLEAR_DATE) = v_current_year
				AND MONTH(r.C_CLEAR_DATE) = v_current_month
				AND r.C_REJECTED = 0
				AND r.C_CLEAR_DATE IS NOT NULL
				AND r.C_BIZ_DAYS_EXCEED < 0
				AND rt.C_SPEC_CODE = v_spec_code
				AND r.FK_VILLAGE_ID = v_village_id
	);
	#Tong so hoso dang thu ly dung tien do, tinh den ngay bao cao		
	SET v_dang_thu_ly_dung_tien_do 
		= (SELECT COUNT(DISTINCT r.pk_record)    
			FROM view_processing_record r   
				LEFT JOIN t_r3_record_type rt 
				ON r.fk_record_type = rt.pk_record_type
					LEFT JOIN (SELECT fk_record, c_done FROM t_r3_record_supplement WHERE c_done = 0) rs
					ON rs.FK_RECORD =r.PK_RECORD                                
			WHERE 
				(DATEDIFF(v_current_date, r.C_DOING_STEP_DEADLINE_DATE) <= 0)
				AND rs.FK_RECORD IS NULL
				AND (r.C_NEXT_TASK_CODE IS NOT NULL)
				AND r.C_BIZ_DAYS_EXCEED IS NULL
				AND (DATEDIFF(v_current_date, r.C_RETURN_DATE) <= 0)
				AND rt.C_SPEC_CODE = v_spec_code
				AND r.C_IS_PAUSING <> 1
				AND r.FK_VILLAGE_ID = v_village_id
	);
	#Tong so hoso dang thu ly CHAM tien do, tinh den ngay bao cao
	SET v_dang_thu_ly_cham_tien_do 
		= (SELECT COUNT(DISTINCT r.pk_record)    
			FROM view_processing_record r
				INNER JOIN t_r3_record_type rt 
				ON r.fk_record_type = rt.pk_record_type
					LEFT OUTER JOIN (SELECT fk_record, c_done FROM t_r3_record_supplement WHERE c_done = 0) rs
					ON rs.FK_RECORD =r.PK_RECORD 
			WHERE 
				(DATEDIFF(v_current_date, r.C_DOING_STEP_DEADLINE_DATE) > 0)                                     
				AND (r.C_NEXT_TASK_CODE IS NOT NULL)
				AND rs.FK_RECORD IS NULL
				AND (DATEDIFF(v_current_date, r.C_RETURN_DATE) <= 0)    
				AND r.C_BIZ_DAYS_EXCEED IS NULL
				AND rt.C_SPEC_CODE = v_spec_code
				AND r.C_IS_PAUSING <> 1
				AND r.FK_VILLAGE_ID = v_village_id
	);	
	#Tong so ho so phai bo sung, tinh den ngay bao cao				
	SET v_phai_bo_sung 
		= (SELECT COUNT(DISTINCT r.PK_RECORD)    
			FROM t_r3_record_supplement rs
				LEFT JOIN view_processing_record r
				ON rs.FK_RECORD =r.PK_RECORD 
					INNER JOIN t_r3_record_type rt 
					ON r.fk_record_type = rt.pk_record_type
			WHERE 
				rs.C_DONE = 0 
				AND rs.C_RECEIVE_DATE IS NULL
				AND rt.C_SPEC_CODE = v_spec_code
				AND r.FK_VILLAGE_ID = v_village_id
	);
	#Tong so hoso dang thu ly, QUA HAN, tinh den ngay bao cao
	SET v_thu_ly_qua_han 
		= (SELECT COUNT(DISTINCT r.pk_record)    
			FROM view_processing_record r
				INNER JOIN t_r3_record_type rt 
				ON r.fk_record_type = rt.pk_record_type
					LEFT OUTER JOIN (SELECT fk_record, c_done FROM t_r3_record_supplement WHERE c_done = 0) rs
					ON rs.FK_RECORD =r.PK_RECORD 
			WHERE (DATEDIFF(v_current_date, r.C_RETURN_DATE) > 0)                                     
				AND r.C_BIZ_DAYS_EXCEED IS NULL
				AND rs.FK_RECORD IS NULL
				AND rt.C_SPEC_CODE = v_spec_code
				AND r.FK_VILLAGE_ID = v_village_id
	);
	#Tong so hoso bi cong dan rut TRONG THANG, tinh den ngay bao cao
	SET v_cong_dan_rut 
		= (SELECT COUNT(*) 
			FROM view_record r
				INNER JOIN t_r3_record_type rt 
				ON r.fk_record_type = rt.pk_record_type
			WHERE 
				r.C_REJECTED = 2
				AND MONTH(r.C_CLEAR_DATE) = v_current_month
				AND YEAR(r.C_CLEAR_DATE) = v_current_year                                     
				AND rt.C_SPEC_CODE = v_spec_code
				AND r.FK_VILLAGE_ID = v_village_id
	);
	#Tong so hoso bi thu choi TRONG THANG
	# Hanh dong tu choi cua Uy Ban xay ra TRONG THANG
	SET v_bi_tu_choi 
		= (SELECT COUNT(*)    
			FROM view_record r
				INNER JOIN t_r3_record_type rt 
				ON r.fk_record_type = rt.pk_record_type
			WHERE 
				r.C_REJECTED = 1
				AND MONTH(r.C_REJECT_DATE) = v_current_month
				AND YEAR(r.C_REJECT_DATE) = v_current_year                                 
				AND rt.C_SPEC_CODE = v_spec_code
				AND r.FK_VILLAGE_ID = v_village_id
	);
	#Tong so ho so dang chuyen qua THUE, tinh den ngay bao cao
	SET v_thue 
		= (SELECT COUNT(*)    
			FROM view_processing_record r
				INNER JOIN t_r3_record_type rt 
				ON r.fk_record_type = rt.pk_record_type
			WHERE (
				C_NEXT_TASK_CODE LIKE '%NOP_HO_SO_SANG_CHI_CUC_THUE'
				OR C_NEXT_TASK_CODE LIKE '%NHAN_THONG_BAO_CUA_CHI_CUC_THUE'
				OR C_NEXT_TASK_CODE LIKE '%CHUYEN_THONG_BAO_THUE_VE_BP_MOT_CUA'
				OR C_NEXT_TASK_CODE LIKE '%TRA_THONG_BAO_NOP_THUE'
				OR C_NEXT_TASK_CODE LIKE '%NHAN_BIEN_LAI_NOP_THUE'
				OR C_NEXT_NO_CHAIN_TASK_CODE LIKE '%NOP_HO_SO_SANG_CHI_CUC_THUE'
				OR C_NEXT_NO_CHAIN_TASK_CODE LIKE '%NHAN_THONG_BAO_CUA_CHI_CUC_THUE'
				OR C_NEXT_NO_CHAIN_TASK_CODE LIKE '%CHUYEN_THONG_BAO_THUE_VE_BP_MOT_CUA'
				OR C_NEXT_NO_CHAIN_TASK_CODE LIKE '%TRA_THONG_BAO_NOP_THUE'
				OR C_NEXT_NO_CHAIN_TASK_CODE LIKE '%NHAN_BIEN_LAI_NOP_THUE'
			)                                 
			AND rt.C_SPEC_CODE = v_spec_code
			AND r.FK_VILLAGE_ID = v_village_id
	);
	
	#Xoa ho cu neu co
	DELETE FROM t_r3_record_history_stat 
	WHERE DATEDIFF(C_HISTORY_DATE,NOW())=0
        AND C_SPEC_CODE = v_spec_code
        AND FK_VILLAGE_ID = v_village_id;
        
    #Insert thong tin moi
		C_SPEC_CODE
		, C_HISTORY_DATE
		, C_COUNT_TONG_TIEP_NHAN 
		, C_COUNT_TONG_TIEP_NHAN_TRONG_THANG
		, C_COUNT_DANG_THU_LY 
		, C_COUNT_DANG_CHO_TRA_KET_QUA 
		, C_COUNT_DA_TRA_KET_QUA 
		, C_COUNT_DA_TRA_KET_QUA_TRUOC_HAN
		, C_COUNT_DA_TRA_KET_QUA_DUNG_HAN
		, C_COUNT_DA_TRA_KET_QUA_QUA_HAN
		, C_COUNT_DANG_THU_LY_DUNG_TIEN_DO 
		, C_COUNT_DANG_THU_LY_CHAM_TIEN_DO
		, C_COUNT_BO_SUNG
		, C_COUNT_THU_LY_QUA_HAN
		, C_COUNT_CONG_DAN_RUT
		, C_COUNT_TU_CHOI
		, C_COUNT_THUE
		, FK_VILLAGE_ID
	) VALUES (
		v_spec_code
		,DATE_FORMAT(v_current_date,'%Y-%m-%d')
		, v_tong_tiep_nhan 
		, v_tong_tiep_nhan_trong_thang 
		, v_dang_thu_ly 
		, v_dang_cho_tra_kq 
		, v_da_tra_kq 
		, v_da_tra_kq_truoc_han 
		, v_da_tra_kq_dung_han 
		, v_da_tra_kq_qua_han 
		, v_dang_thu_ly_dung_tien_do 
		, v_dang_thu_ly_cham_tien_do 
		, v_phai_bo_sung
		, v_thu_ly_qua_han
		, v_cong_dan_rut
        , v_bi_tu_choi
        , v_thue
        , v_village_id
	);							
END */$$
DELIMITER ;

/* Procedure structure for procedure `sp_swap_user` */

/*!50003 DROP PROCEDURE IF EXISTS  `sp_swap_user` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`web_user`@`%` PROCEDURE `sp_swap_user`( p_from_user_code VARCHAR(100), p_to_user_code VARCHAR(100))
BEGIN
    DECLARE v_to_user_name VARCHAR(50);
    SET v_to_user_name = (SELECT C_NAME FROM t_cores_user WHERE C_LOGIN_NAME = p_to_user_code);
    #1. Ghi swap log, tat ca ROLE
                 C_TO_USER_CODE,
                 C_SWAP_DATE,
                 C_ROLE)
    SELECT DISTINCT
        p_from_user_code,
        p_to_user_code,
        NOW(),
        RIGHT(C_TASK_CODE, LOCATE('::', REVERSE(C_TASK_CODE)) - 1)    C_ROLE
    FROM t_r3_user_task
    WHERE C_USER_LOGIN_NAME = p_from_user_code;
    
    #2 Update XML_PROCESSING next_task
    UPDATE t_r3_record 
    SET C_XML_PROCESSING = UpdateXML(C_XML_PROCESSING, CONCAT('/data/next_task[@user=''', p_from_user_code , ''']/@user_name'), CONCAT('user_name="', v_to_user_name , '"'))
    WHERE C_NEXT_USER_CODE = p_from_user_code;
    
    
    UPDATE t_r3_record
    SET C_XML_PROCESSING = UpdateXML(C_XML_PROCESSING, '/data/next_task/@user', CONCAT('user="',p_to_user_code,'"'))
    WHERE C_NEXT_USER_CODE = p_from_user_code;
    
    #3 update C_NEXT_USER_CODE
    UPDATE t_r3_record
    SET C_NEXT_USER_CODE = REPLACE(C_NEXT_USER_CODE, p_from_user_code, p_to_user_code)
    WHERE CONCAT(',',C_NEXT_USER_CODE,',') LIKE CONCAT('%,',p_from_user_code,',%');
    
    #3B update C_NEXT_USER_CODE
    UPDATE t_r3_internet_record
    SET C_NEXT_USER_CODE = REPLACE(C_NEXT_USER_CODE, p_from_user_code, p_to_user_code)
    WHERE CONCAT(',',C_NEXT_USER_CODE,',') LIKE CONCAT('%,',p_from_user_code,',%');
    
    #4 update C_NEXT_CO_USER_CODE
    UPDATE t_r3_record
    SET C_NEXT_CO_USER_CODE = REPLACE(C_NEXT_CO_USER_CODE, p_from_user_code, p_to_user_code)
    WHERE CONCAT(',',C_NEXT_CO_USER_CODE,',') LIKE CONCAT('%,',p_from_user_code,',%');
    
    #5 update C_NEXT_NO_CHAIN_USER_CODE
    UPDATE t_r3_record
    SET C_NEXT_NO_CHAIN_USER_CODE = REPLACE(C_NEXT_NO_CHAIN_USER_CODE, p_from_user_code, p_to_user_code)
    WHERE CONCAT(',',C_NEXT_NO_CHAIN_USER_CODE,',') LIKE CONCAT('%,',p_from_user_code,',%');
    
    #6 update t_r3_user_task => cong viec nsd duoc phan cong
    UPDATE t_r3_user_task
    SET C_USER_LOGIN_NAME = p_to_user_code
    WHERE C_USER_LOGIN_NAME = p_from_user_code;
END */$$
DELIMITER ;

